mapper.select:
    	SELECT IFNULL(l.l_check,0) AS l_check FROM posts AS p 
    	LEFT JOIN (SELECT * FROM likes WHERE l_uno = #{uNo}) AS l ON p_no = l_pno
		WHERE p_no = #{pNo};

SELECT IFNULL(f.f_check, 0) AS f_check FROM posts AS p 
LEFT JOIN (SELECT * FROM follows WHERE f_follower_no = #{uNo}) AS f ON p_uno = f_followed_no
WHERE p_no = #{pNo};

    	SELECT IFNULL(r.r_check, 0) AS r_check FROM posts AS p 
    	LEFT JOIN (SELECT * FROM repositorys WHERE r_uno = #{uNo}) AS r ON r_pno = p_no
		WHERE p_no = #{pNo};
